import time

from machine import Pin, SPI

# Registers ----------------------------------------------------------------------------------------

# FIXME: Try to reuse csr.csv generated by LiteX.
# FIXME: Increase similarities with LiteX-Server.
CSR_LEDS       = 0x00001000
CSR_IDENTIFIER = 0x00000800

# SPI Bone -----------------------------------------------------------------------------------------

class SPIBone:
    def __init__(self, pins={"sck": 10, "csn": 13, "mosi": 11, "miso": 12}, clk_freq=1e6):
        self.csn = Pin(pins["csn"], Pin.OUT, value=1)
        self.spi = SPI(1, baudrate=int(clk_freq), polarity=0, phase=0,
            sck  = Pin(pins["sck"]),
            mosi = Pin(pins["mosi"]),
            miso = Pin(pins["miso"])
        )

    def write(self, address, value):
        self.csn.low()
        self.spi.write(int(0x0).to_bytes(1, "big"))
        self.spi.write( address.to_bytes(4, "big"))
        self.spi.write(   value.to_bytes(4, "big"))
        self.spi.read(1)
        self.csn.high()

    def read(self, address):
        self.csn.low()
        self.spi.write(int(0x1).to_bytes(1, "big"))
        self.spi.write( address.to_bytes(4, "big"))
        self.spi.read(1)
        value = int.from_bytes(self.spi.read(4), 4, "big")
        self.csn.high()
        return value

bus = SPIBone()

# Demo ---------------------------------------------------------------------------------------------

def identifier_demo():
    print("Identifier:")
    identifier = ""
    for i in range(256):
        c = chr(bus.read(CSR_IDENTIFIER + 4*i) & 0xff)
        identifier += c
        if c == "\0":
            break
    print(identifier)

def led_demo():
    print("Led Demo:")

    print("Counter mode...")
    for i in range(16):
        bus.write(CSR_LEDS, i)
        time.sleep(0.1)

    print("Shift mode...")
    for i in range(8):
        bus.write(CSR_LEDS, 1<<i)
        time.sleep(0.1)
    for i in reversed(range(8)):
        bus.write(CSR_LEDS, 1<<i)
        time.sleep(0.1)

    print("Dance mode...\n");
    for i in range(4):
        bus.write(CSR_LEDS, 0x55)
        time.sleep(0.2)
        bus.write(CSR_LEDS, 0xaa)
        time.sleep(0.2)

identifier_demo()
led_demo()